import styled from "styled-components";


export const Container = styled.div`
    height: 900px;
    display: block;
    background: #f9f9f9;
    padding: 0 13rem;
    padding-top: 10rem ;

`
export const Card = styled.div`
    background: #fff;
    display: block;
	height: 900px;
	z-index:0 ;
    padding-top: 0.7rem;
    padding-bottom: 10rem;
    transition: all 0.2s ease-in-out;
    box-shadow: 0 0.7px 1px rgba(0,0,0,0.2);
`

export const SearchH1 = styled.h1`
    font-size: 3rem;
    color: #01bf71;
	padding-top:2rem ;
	text-align:center;
	display: block;
    
    @media screen and (max-width: 480px) {
        font-size: 2rem;
    }
`

export const Form = styled.form`

font-family: sans-serif;
	width: 700px;
	align-items: center;
    .search-bar {
  position: absolute;
  top: 50%;
  left: 50%;
  display: flex;
  transform: translate(-50%,-50%);
  background: #6a89cc;
  width: 500px;
  height: 40px;
  padding: 0.2rem 0.6rem 0.2rem 0.2rem;  
  box-sizing: border-box;
  box-shadow: 1px 1px 0 0 #4a69bd,
              2px 2px 0 0 #4a69bd,
              3px 3px 0 0 #4a69bd,
              4px 4px 0 0 #4a69bd,
              5px 5px 0 0 #4a69bd,
              6px 6px 0 0 #4a69bd
}

.search-bar input {
  border: 0;
  outline: none;
  height: 100%;
  width: 85%;
  padding: 0 1rem;
  font-size: 1rem;
  font-style: italic;
}

.search-bar button {
  margin-left: auto;
  background: none;
  border: none;
  color: white;
  font-weight: bolder;
  font-size: 1.2rem;
  cursor: pointer;
  padding: 0;
  outline: none;
}


    @media screen and (max-width: 480px) {
        font-size: 2rem;
    }
`
